package com.dracolibros;

import androidx.test.ext.junit.runners.AndroidJUnit4;

import com.dracolibros.model.BookEntity;
import com.dracolibros.model.BookModel;
import static org.junit.Assert.assertEquals;

import org.junit.Before;
import org.junit.Test;
import org.junit.runner.RunWith;

import java.util.ArrayList;

@RunWith(AndroidJUnit4.class)
public class ModelTest {
    private BookModel bookModel;
    private BookEntity bookEntity;
    private String id;


    @Before
    public void setUp(){
        bookModel = new BookModel();
        bookEntity = new BookEntity();
        bookEntity.setId("27");
        bookEntity.setName("Dragones rojos");
        bookEntity.setAuthor("Jorge Sánchez");
        bookEntity.setCode("0123ABC");
        bookEntity.setIsbn("999-87-45633-12-1");
        bookEntity.setDate("1/1/1995");
        bookEntity.setImage("");
        bookEntity.setGenre("Fantasía");
        bookEntity.setAvailable(true);
        bookModel.insert(bookEntity);
        this.id = this.bookModel.getAllSummarize().get(5).getId();
    }

    @Test
    public void insertBook(){
        assertEquals(true, this.bookModel.insert(this.bookEntity));
    }

    @Test
    public void updateBook(){
        this.bookEntity.setAvailable(false);
        assertEquals(true, this.bookModel.insert(this.bookEntity));
    }

    @Test
    public void getAllBooks(){
        assertEquals(this.id, this.bookModel.getAllSummarize().get(5).getId());
    }


    @Test
    public void getBookWithId(){
        assertEquals(this.id, this.bookModel.getbyid(this.id).getId());
        assertEquals(this.bookEntity.getName(), this.bookModel.getbyid(this.id).getName());
    }


    @Test
    public void getAllGenres(){
        ArrayList<String> genres = new ArrayList<>();
        genres.add("Fantasía");
        genres.add("Romántica");
        genres.add("Histórica");
        genres.add("CienciaFicción");
        genres.add("Bibliografía");
        assertEquals(genres, this.bookModel.getGenres());
    }

    @Test
    public void searchBook(){
        ArrayList<BookEntity> books = new ArrayList<>();
        books.add(this.bookEntity);
        assertEquals(books.get(0).getName(), this.bookModel.getWithFilter("Dragones rojos", "1/1/1995", "Fantasía").get(0).getName());
        assertEquals(0, this.bookModel.getWithFilter("El castillo azul", "", "").size());
    }

    @Test
    public void deleteBook(){
        assertEquals(false, this.bookModel.delete(bookEntity));
    }
}
