    @Override
    protected void onActivityResult(int requestCode, int resultCode, @Nullable Intent data) {
        super.onActivityResult(requestCode, resultCode, data);
        if(resultCode == RESULT_CANCELED){
            System.out.println("Back");
        }else{
            if(!data.getExtras().getString("name").equals("")){
                nameFilter = data.getExtras().getString("name");
            }else{

            }

            if(!data.getExtras().getString("date").equals("")){
                    dateFilter = data.getExtras().getString("date");
            }
            if(data.getExtras().getLong("spinner")!=0){
                ArrayList<String> items = presenter.getGenres();
                genreFilter = items.get((int)data.getExtras().getLong("spinner")-1);
            }
        }
    }

    @Override
    protected void onResume() {
        Log.d(TAG, "Starting onResume");
        super.onResume();

        if(nameFilter==null && dateFilter==null && genreFilter==null){
            items = presenter.getAllSummarize();
            adapter.notifyDataSetChanged();
        }else{
            items = presenter.getItemsFilter(nameFilter, dateFilter, genreFilter);
            nameFilter = null;
            dateFilter = null;
            genreFilter = null;
            adapter.notifyDataSetChanged();
        }
        size=(TextView) findViewById(R.id.ResultBook);
        size.setText(items.size()+getString(R.string.result));

        // Inicializa el RecyclerView
        final RecyclerView recyclerView = (RecyclerView) findViewById(R.id.infobook);

        // Crea el Adaptador con los datos de la lista anterior
        adapter = new BookAdapter(items);

        // Asocia el elemento de la lista con una acción al ser pulsado
        adapter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Acción al pulsar el elemento
                int position = recyclerView.getChildAdapterPosition(v);
                /*Toast.makeText(ListActivity.this, "Posición: " + String.valueOf(position) +" Name: " + items.get(position).getName(), Toast.LENGTH_SHORT)
                        .show();
                 */
                presenter.onClickRecyclerViewItem(items.get(position).getId());
            }
        });

        // Asocia el Adaptador al RecyclerView
        recyclerView.setAdapter(adapter);

        // Muestra el RecyclerView en vertical
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

    }

