package com.dracolibros.views;

import android.content.Intent;
import android.os.Bundle;

import androidx.appcompat.app.AppCompatActivity;
import androidx.appcompat.widget.Toolbar;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.util.Log;

import android.view.Menu;
import android.view.MenuItem;
import android.view.View;
import android.widget.Toast;

import com.dracolibros.R;
import com.dracolibros.interfaces.ListInterface;
import com.dracolibros.model.BookEntity;
import com.dracolibros.presenters.ListPresenter;
import com.google.android.material.floatingactionbutton.FloatingActionButton;

import java.sql.Array;
import java.util.ArrayList;

public class ListBookActivity extends AppCompatActivity implements ListInterface.View {

    private ListInterface.Presenter presenter;
    private ArrayList<BookEntity> items;

    String TAG = "Dracolibros/ListBookActivity";

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        Log.d(TAG, "Starting onCreate");
        super.onCreate(savedInstanceState);

        Log.d(TAG, "Starting layout");
        setContentView(R.layout.activity_listbook);

        Log.d(TAG, "Starting toolbar");
        Toolbar toolbar = findViewById(R.id.toolbar);
        setSupportActionBar(toolbar);

        presenter = new ListPresenter(this);

        Log.d(TAG, "Starting FloatingActionButton");
        FloatingActionButton newbook = findViewById(R.id.newbook);
        newbook.setOnClickListener(new View.OnClickListener() {
        @Override
        public void onClick(View view) {
            presenter.onClickFloatingButton();
        }
        });

        //___________________________________________________________NUEVO
        // Crea una lista con los elementos a mostrar
        items = new ArrayList<BookEntity>();
        BookEntity book1 = new BookEntity();
        book1.setId("1");
        book1.setName("Dragones rojos");
        book1.setImage("");
        items.add(book1);
        BookEntity book2 = new BookEntity();
        book2.setId("2");
        book2.setName("Ojo del terror");
        book2.setImage("");

        items.add(book2);

        // Inicializa el RecyclerView
        final RecyclerView recyclerView = (RecyclerView) findViewById(R.id.infobook);

        // Crea el Adaptador con los datos de la lista anterior
        BookAdapter adapter = new BookAdapter(items);

        // Asocia el elemento de la lista con una acción al ser pulsado
        adapter.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // Acción al pulsar el elemento
                int position = recyclerView.getChildAdapterPosition(v);
                /*
                Toast.makeText(ListBookActivity.this, "Posición: " + String.valueOf(position) + " Name: " + items.get(position).getName() + " Author: " + items.get(position).getAuthor(), Toast.LENGTH_SHORT)
                        .show();
                 */
                presenter.onClickRecyclerViewItem(items.get(position).getId());
            }
        });

        // Asocia el Adaptador al RecyclerView
        recyclerView.setAdapter(adapter);

        // Muestra el RecyclerView en vertical
        recyclerView.setLayoutManager(new LinearLayoutManager(this));

    }

    @Override
    protected void onResume() {
        Log.d(TAG, "Starting onResume");
        super.onResume();
    }

    @Override
    protected void onStart() {
        Log.d(TAG, "Starting onStart");
        super.onStart();
    }

    @Override
    protected void onPause() {
        Log.d(TAG, "Starting onPause");
        super.onPause();
    }

    @Override
    protected void onStop() {
        Log.d(TAG, "Starting onStop");
        super.onStop();
    }

    @Override
    protected void onRestart() {
        Log.d(TAG, "Starting onRestart");
        super.onRestart();
    }

    @Override
    protected void onDestroy() {
        Log.d(TAG, "Starting onDestroy");
        super.onDestroy();
    }

    @Override
    public boolean onCreateOptionsMenu(Menu menu) {
        getMenuInflater().inflate(R.menu.menu_main, menu);
        return true;
    }

    @Override
    public boolean onOptionsItemSelected(MenuItem item) {
        int id = item.getItemId();

        if (id == R.id.settings) {
            Log.d(TAG, "Starting Settings");
            return true;
        }

        if (id == R.id.action_search) {
            Log.d(TAG, "Starting search");
            presenter.onClickSearchButton();
            return true;
        }

        if (id == R.id.action_Help) {
            Log.d(TAG, "Starting Help");
            return true;
        }

        if (id == R.id.Order) {
            Log.d(TAG, "Starting Order");
            return true;
        }

        if (id == R.id.action_AppDL) {
            Log.d(TAG, "Starting AppDL");
            presenter.onClickAboutAPPButton();
            return true;
        }

        return super.onOptionsItemSelected(item);
    }

    @Override
    public void StartFormActivity() {
        Log.d(TAG, "Starting FormActivity");
        Intent intent = new Intent(getApplicationContext(), FormActivity.class);
        startActivity(intent);
    }

    //_________________________NUEVO2
    @Override
    public void StartFormActivity(String id) {
        Log.d(TAG, "Starting FormActivity with id....");
        Intent intent = new Intent(getApplicationContext(), FormActivity.class);
        intent.putExtra("id", id);
        startActivity(intent);
    }

    @Override
    public void StartSearchActivity() {
        Log.d(TAG, "Starting SearchActivity");
        Intent intent = new Intent(getApplicationContext(), SearchActivity.class);
        startActivity(intent);
    }

    @Override
    public void StartAboutAPPActivity() {
        Log.d(TAG, "Starting AboutAPPActivity");
        Intent intent = new Intent(getApplicationContext(), AboutAPPActivity.class);
        startActivity(intent);
    }
}